const int lz::Path::_srcEidOffset = _HEADER_WIDTH;
const int lz::Path::_srcOAppOffset = lz::Path::_srcEidOffset + 32;
const int lz::Path::_dstEidOffset = lz::Path::_srcOAppOffset + 256;
