const int _QUERY_ID = 4;
const int _BALANCE = 5;
const int _MSG_VALUE = 6;
const int _BODY = 7;
const int _RAW_MSG = 8;
const int _ORIGIN = 9;
const int _DONATION_NANOS = 10;
const int _MD = 11;

